package com.supoin.framesdk.utils;public class IntegerUtils {	public static String toHexColorStr(int n) {		if (n < 0) {			n = 0;		}		if (n > 255) {			n = 255;		}		String hexN = Integer.toHexString(n);		if (hexN.length() == 1) {			hexN = "0" + hexN;		}		return hexN;	}	public static float to0_255to0_1(float n) {		float f = 0f;		if (n < 0) {			f = 0f;		} else if (n > 255) {			f = 1f;		} else {			f = n / 255;		}		return f;	}}